/* shaders and textures */
export const ballTexture = "data:image/png;base64,";

export const pointVertexShader = `
attribute float kolor;
attribute float opacity;
attribute float size;
attribute float show;
    
uniform vec3 colorLUT[256];
uniform vec2 colorDomain;
uniform vec2 colorTarget;
uniform float colorGamma;

uniform vec2 opacityDomain;
uniform vec2 opacityTarget;
uniform float opacityGamma;

uniform vec2 radiusDomain;
uniform vec2 radiusTarget;
uniform float radiusGamma;

varying vec3 vColor;
varying float vOpacity;
varying float vVisible;

float transformVal(float val, vec2 domain, vec2 target, float gamma) {
    float domainSpan = domain.y - domain.x;
    float targetSpan = target.y - target.x;

    float xVal = val;

    if (abs(domainSpan) < 1e-9 || abs(targetSpan) < 1e-9) {
        xVal = (target.x + target.y) / 2.0;
    } else {
        xVal = target.x + targetSpan * (val - domain.x)/domainSpan;
    }

    return pow(xVal, gamma);
}

void main() {
    int colorIdx = int(255.0 * transformVal(kolor, colorDomain, colorTarget, colorGamma));
    vColor = colorLUT[colorIdx];
    vOpacity = transformVal(opacity, opacityDomain, opacityTarget, opacityGamma);
    float xSize = 400.0 * transformVal(size, radiusDomain, radiusTarget, radiusGamma);
    vVisible = show;  // a problem!
    // vVisible = show;
    vec4 mvPosition = modelViewMatrix * vec4( position, 1.0 );
    gl_PointSize = xSize * ( 3000.0 / -mvPosition.z );
    gl_Position = projectionMatrix * mvPosition;
}`;

export const pointFragmentShader = `
uniform sampler2D pointTexture;
varying vec3 vColor;
varying float vOpacity;
varying float vVisible;

void main() {
    gl_FragColor = vec4( vColor, vOpacity * vVisible );
    gl_FragColor = gl_FragColor * texture2D( pointTexture, gl_PointCoord );
}`;

export const compartmentVertexShader =  `
varying vec3 normal_in_camera;
varying vec3 view_direction;

void main() {
    vec4 pos_in_camera = modelViewMatrix * vec4(position, 1.0);
    gl_Position = projectionMatrix * pos_in_camera;
    normal_in_camera = normalize(mat3(modelViewMatrix) * normal);
    view_direction = normalize(pos_in_camera.xyz);
}`;

export const compartmentFragmentShader = `
uniform vec3 color;
varying vec3 normal_in_camera;
varying vec3 view_direction;

void main() {
    // Make edges more opaque than center
    float edginess = 1.0 - abs(dot(normal_in_camera, view_direction));
    float opacity = clamp(edginess - 0.30, 0.0, 0.5);
    // Darken compartment at the very edge
    float blackness = pow(edginess, 4.0) - 0.3;
    vec3 c = mix(color, vec3(0,0,0), blackness);
    gl_FragColor = vec4(c, opacity);
}`;

